(()=>{let e=document.getElementById("debug_msg"),t=document.createElement("video");try{navigator.mediaDevices.getUserMedia({audio:!1,video:{mandatory:{minWidth:1280,maxWidth:1920,minHeight:720,maxHeight:1080}}}).then((n=>{e.innerText+="\nStarting local stream...\n",t.srcObject=n,t.muted=!0,t.play()})).catch((t=>{e.innerText+="[Error] Can't start local stream\n",e.innerText+="[Error] "+t.name+": "+t.message+"\n"}))}catch(t){e.innerText+="\n [Error] Can't load media\n",e.innerText+="[Error] "+t.name+": "+t.message+"\n"}document.body.appendChild(t)})();